INSERT INTO product (id,name,price,description) VALUES (1,'Assassins Creed Valhalla',50.99,'Ambientado en el siglo IX, el juego tiene lugar durante la invasion de Gran Bretanha  por parte de los Vikingos, con el jugador controlando al guerrero vikingo Eivor dirigiendo a su pueblo contra el rey sajón Alfredo el Grande.')
INSERT INTO product (id,name,price,description) VALUES (2,'Minecraft', 60.99,'Minecraft es un videojuego de construccion de tipo «mundo abierto o sandbox creado originalmente por el sueco Markus Persson (conocido comúnmente como Notch y posteriormente desarrollado por Mojang Studios (actualmente parte de Microsoft).')
INSERT INTO product (id,name,price,description) VALUES (3,'Assassins Creed Odyssey',30.99,'Está ambientado dentro de los años 431 a 422 AC, narra una historia acerca de la Guerra del Peloponeso entre Atenas y Esparta, concretamente en la Antigua Grecia, cuna de la civilización occidental. ')
INSERT INTO product (id,name,price,description) VALUES (4,'Poppy Playtime', 8.99, 'Poppy Playtime es un survival de horror en primera persona en el que los jugadores interpretan a un exempleado de Playtime company que regresa a la fábrica de juguetes abandonada de dicha empresa después de recibir una carta del personal')
INSERT INTO product (id,name,price,description) VALUES (5,'Red Dead Redemption', 45.99, 'Red Dead Redemption 2 es un videojuego de acción-aventura de temática western ambientado en un entorno de mundo abierto y jugado desde una perspectiva en tercera persona con componentes para un jugador y multijugador en línea.');
INSERT INTO product (id,name,price,description) VALUES (6,'Call Of Dutty BO3', 36.99,'Call of Duty: Black Ops 3 tiene lugar en el año 2065, 40 años después de los acontecimientos de Black Ops 2 , en un mundo que enfrenta la problemática del cambio climático y las nuevas tecnologías.' )